import React, { useEffect, useState } from "react";
import "./SearchMovie.css";
import MoviesModel from "../../../Models/movies-model";
import moviesService from "../../../Services/movies-service";
import notify from "../../../Utils/Notify";
import appConfig from "../../../Utils/AppConfig";
import { NavLink } from "react-router-dom";
import { Button, Card } from "react-bootstrap";

function SearchMovie(): JSX.Element {

    useEffect(() => {
        moviesService.getAllMovies()
            .then(movie => setMovieList(movie))
            .catch(err => notify.error(err))
    }, [])

    const [text, setText] = React.useState<string>('');
    const [MovieList, setMovieList] = useState<MoviesModel[]>()

    const handleOnClick1 = () => {
        const findMovies =
            MovieList && MovieList?.length > 0 ? MovieList?.filter((s) => s?.name == text) : undefined
        console.log(findMovies)
        setMovieList(findMovies)
    }
    const {getItemQuantity} = useS
    const quantity = 0;
    return (
        <div className="SearchMovie">
            <h1>movie find</h1>
            <div className="input_wrapper">
                <input
                    type="text"
                    placeholder="search movie"
                    value={text}
                    onChange={(e) => {

                        setText(e.target.value);
                        setMovieList(MovieList);
                    }
                    }
                />
                <button disabled={!text} onClick={handleOnClick1}>search</button>
            </div>

            <div className="body">

                {MovieList && MovieList?.length == 0 && (
                    <div className="notFound">Movie not found</div>
                )}

                {MovieList && MovieList?.length > 0 &&
                    MovieList?.map(user => {
                        return (
                            <div className="body_item">
                                <div className="DataCard">
                                    <div className="poster">
                                        <img className="img" src={appConfig.moviesImagesUrl + user.imageName} />
                                    </div>
                                    <div className="details">
                                        <h4>שם: {user.name}
                                            <br />
                                            זמן: {user.time.slice(0, 10).split("-").reverse().join("/")}
                                            <br />
                                            דקות: {user.length}
                                            <br />
                                            תקציר : {user.summary}

                                            <br />



                                            {quantity === 0 ? (
                                                <button onClick={() => alert("hello")}>+ הוסף לסל</button>
                                            ) : (
                                                <div
                                                >
                                                    <div

                                                    >
                                                        <button>-</button>
                                                        <span>{MovieList.length}</span>  in cart
                                                        <button>+</button>
                                                    </div>
                                                    <Button variant="danger" size="sm">remove</Button>

                                                    <br />
                                                </div>
                                            )}


                                            <NavLink to="/buyMovie">לרכישה</NavLink>
                                        </h4>
                                    </div>

                                </div>
                            </div>
                        )
                    })}
            </div>

        </div >
    );
}

export default SearchMovie;
