import React, { ChangeEvent, SyntheticEvent, useEffect, useState } from "react";
import notify from "../../../Utils/Notify";
import "./DataList.css";
import { request } from "http";
import DataCard from "../DataCard/DataCard";
import TheatersModel from "../../../Models/theatersId-model";
import MoviesModel from "../../../Models/movies-model";
import moviesService from "../../../Services/movies-service";



function DataList(): JSX.Element {

    const [thread, setThread] = useState<TheatersModel[]>([]);
    const [movie, setMovie] = useState<MoviesModel[]>([]);

    useEffect(() => {
        moviesService.getAllTheaters()
            .then(thread => setThread(thread))
            .catch(err => notify.error(err))
    }, [])


    async function name(args:ChangeEvent<HTMLSelectElement>) {
        try{
            const theaterId = +args.target.value;
            const dbTheater = await moviesService.getMovieByTheater(theaterId)
            setMovie(dbTheater)
        }
        catch(err:any){
            notify.error(err)
        }
    }

    async function deleteMe(movieId: number) {
        try {
            if (!window.confirm("Are you sure?")) return;
            await moviesService.deleteMovie(movieId);
            notify.success("Gift has been deleted");

            // Refresh list: 
            const duplicateGifts = [...movie];
            const index = duplicateGifts.findIndex(m => m.movieId === movieId);
            duplicateGifts.splice(index, 1);
            setMovie(duplicateGifts);
        }
        catch (err: any) {
            notify.error(err);
        }
    }



    // const movies = students.find(function(students: {
    //     name:string
    //     age:number

    // }): string | undefined{
    //     if(students.age === 24){
    //         return students.name
    //     }
    // }
    // )
    // console.log(movies)

        const students = [
           { name:"haron", age:24},
           { name:"dani", age:23}

        ]

    const[text,setText] = React.useState('');
    const[studentList,setStudentList] = useState<{name:string , age:number} [] | undefined >(students);

    const handleOnClick = () =>{
        const findUsers = 
        studentList && studentList?.length > 0 ? studentList?.filter((s) => s?.name == text) : undefined
        console.log(findUsers)
        setStudentList(findUsers)
    }
    return (
        <div className="DataList">
            <h1>list</h1>

            <h1>user find</h1>
            <div className="input_wrapper">
                <input 
                type="text"
                placeholder="search user"
                value={text}
                onChange={(e) => {

                    setText(e.target.value);
                    setStudentList(students);
                }
                }
                />
                <button disabled ={!text} onClick={handleOnClick}>search</button>
            </div>

            <div className="body">
                {studentList && studentList?.length > 0 && 
                studentList?.map(user =>{
                    return (
                        <div className="body_item Box">
                            <h3>Name: {user?.name}</h3>
                            <p>Age: {user?.age}</p>
                        </div>
                    )
                })}
            </div>

            <br />
            <br />

            <select defaultValue="" onChange={name}>
                {<option disabled value="">select theater</option>}
                {thread.map(t => <option key={t.theaterId} value={t.theaterId}>{t.theaterName}</option>)}
            </select>
            <br />
            <br />
            <br />
            {movie.map(m => <DataCard key={m.movieId} movie={m} deleteMe={deleteMe}/>)} 
        </div>
    );
}

export default DataList;
