import { createStore } from "redux";
import MoviesModel from "../Models/movies-model";
import TheatersModel from "../Models/theatersId-model";
import GenreModel from "../Models/genre-model";
import ChairModel from "../Models/chair-model";

export class ChairState {
    public chair: ChairModel[] = [];
}

export enum ChairActionType {
    getAllChairs = "getAllChairs",
}

export interface ChairAction {
    type: ChairActionType;
    payload: any;
}

export function chairReducer(currentState = new ChairState(), action: ChairAction): ChairState {
    const newState = { ...currentState }

    switch (action.type) {
        case ChairActionType.getAllChairs:
            newState.chair = action.payload
            break;
    }
    return newState
}

export const chairStore = createStore(chairReducer);
