import { useState } from "react";
import "./DataCard.css";
import dataService from "../../../Services/DataService";
import notify from "../../../Utils/Notify";
import MovieModel from "../../../Models/movie-model";
import { NavLink } from "react-router-dom";

interface propsMovieCard{
    movie:MovieModel
}

function DataCard(props:propsMovieCard): JSX.Element {

    const[movie,setMovie] = useState<MovieModel>();

    async function deleteMovie() {
        try {

            const sure = window.confirm("Are you sure?");
            if (!sure) return;

            await dataService.deleteMovie(movie.movieId);
            notify.success("Movie has been deleted");
        }
        catch (err: any) {
            notify.error(err.message);
        }
    }


    return (
        <div className="DataCard Box">
            Name: {props.movie.name}
            <br />
            Time: {props.movie.time.slice(0, 10).split("-").reverse().join("/")}
            <br />
            Length: {props.movie.length}
            <br />
            <NavLink to = "#" onClick={deleteMovie}>‚ùå</NavLink>
        </div>
    );
}

export default DataCard;
