import { useState } from "react";
import BlogModel from "../../../Models/blog-model";
import "./BlogCard.css";
import appConfig from "../../../Utils/AppConfig";
import { NavLink } from "react-router-dom";
import { View } from "react-native";
import { CreditCardInput, LiteCreditCardInput } from "react-native-credit-card-input";

interface PropsBlog {
    blog: BlogModel,
}

function BlogCard(props: PropsBlog): JSX.Element {


 const USE_LITE_CREDIT_CARD_INPUT = false


    const [blog, setBlog] = useState<BlogModel[]>([]);




    return (
        <div className="BlogCard">
            <p>-------------------------------------------------------------------------------------------------</p>


            <NavLink to={"/DetailsBlog/" + props.blog.blogId}>
                <h3>{props.blog.name}</h3>

                <div className="poster">
                    <img className="img" src={appConfig.moviesImagesUrl + props.blog.imageName} />
                    <p>{props.blog.title}</p>
                </div>
            </NavLink>

            render() {



            <View>
                {USE_LITE_CREDIT_CARD_INPUT ?
                    (<LiteCreditCardInput />) : (<CreditCardInput />)
                }
            </View>


}
        </div>
    );
}

export default BlogCard;
