import { useEffect, useState } from "react";
import ChairModel from "../../../Models/chair-model";
import "./Chairs.css";
import notify from "../../../Utils/Notify";
import moviesService from "../../../Services/movies-service";
import appConfig from "../../../Utils/AppConfig";
import { useParams } from "react-router-dom";
import ChairCard from "../ChairCard/ChairCard";
import { NavLink } from "react-router-dom";
import BuyModel from "../../../Models/buy-model";
import BuyMovieCard from "../BuyMovieCard/BuyMovieCard";



function Chairs(): JSX.Element {

    const [chair, setChair] = useState<ChairModel[]>([]);
    const [buy, setBuy] = useState<BuyModel[]>([]);

    const params = useParams();

    // useEffect(() => {
    //     moviesService.getAllBuysAfterBuy()
    //         .then(chair => setBuy(chair))
    //         .catch(err => notify.error(err))
    // }, [])

    useEffect(() => {
        moviesService.getAllChairs()
            .then(chair => setChair(chair))
            .catch(err => notify.error(err))
    }, [])

    function favoriteChairs() {
        // const favoriteVacation = +args.target.value;
        moviesService.getAllChairsAfterBuy()
            .then(favoriteChairs => setChair(favoriteChairs))
            .catch(err => alert(err.message))
    }


    return (
        <div className="Chairs">
            <h1>chairs</h1>
            <div className="cardDiv">
                {buy.map((item) => <BuyMovieCard key={item.buyId} buy={item} />)}
                {chair.map((item) => <ChairCard  key={item.chairId} chair={item} buy={new BuyModel}/>)}
            </div>
            <select defaultValue="" onClick={favoriteChairs}>
                <option disabled value="">כסאות</option>
                <option value={5}>כסאות שבחרת</option>
            </select>
            <button>
                <a>
                    <NavLink to={"/buyMovie"}>חזרה</NavLink>
                </a>
            </button>
        </div>

    );
}

export default Chairs;
