import { createStore } from "redux";
import MoviesModel from "../Models/movies-model";
import TheatersModel from "../Models/theatersId-model";
import GenreModel from "../Models/genre-model";
import BlogModel from "../Models/blog-model";

export class BlogState{
    public blog: BlogModel[] = [];
}

export enum BlogActionType{
    getAllBlogs = "getAllBlogs",
}

export interface BlogAction {
    type: BlogActionType;
    payload: any;
}

export function blogReducer(currentState = new BlogState(), action: BlogAction): BlogState {
    const newState = { ...currentState }

    switch (action.type) {
        case BlogActionType.getAllBlogs:
            newState.blog = action.payload
            break;
    }
        return newState
    }

    export const blogStore = createStore(blogReducer);
