import { useEffect, useState } from "react";
import "./DetailsBuy.css";
import BuyModel from "../../../Models/buy-model";
import { useParams } from "react-router-dom";
import moviesService from "../../../Services/movies-service";
import notify from "../../../Utils/Notify";
import appConfig from "../../../Utils/AppConfig";
import BuyMovieCard from "../BuyMovieCard/BuyMovieCard";

function DetailsBuy(): JSX.Element {

    const[buy,setBuy] = useState<BuyModel>();
    const params = useParams();


    useEffect(() => {
        moviesService.getAllBuys(+params.buyId)
        .then(buy => setBuy(buy))
        .catch(err => notify.error(err))
    },[])

    return (
        <div className="DetailsBuy">
			 {buy && <>
                <h3>{buy.title}</h3>
                <div className="poster">
                    <img className="img" src={appConfig.moviesImagesUrl + buy.imageName}/>
                </div>
                <div className="movie">{item => <BuyMovieCard buy={item}  />}</div>                   

                <p>--------------------------------------------------------------------------</p>


            </>}
        </div>
    );
}

export default DetailsBuy;
