import { createStore } from "redux";
import MoviesModel from "../Models/movies-model";
import TheatersModel from "../Models/theatersId-model";
import GenreModel from "../Models/genre-model";
import BuyModel from "../Models/buy-model";

export class BuyState{
    public buy: BuyModel[] = [];
}

export enum BuyActionType{
    getAllBuys = "getAllBuys",
    buy = "buy",
    unBuy = "unBuy",
    AddBuy = "AddBuy",
    RemoveBuy = "RemoveBuy",
}

export interface BuyAction {
    type: BuyActionType;
    payload: any;
}

export function buyReducer(currentState = new BuyState(), action: BuyAction): BuyState {
    const newState = { ...currentState }

    switch (action.type) {
        case BuyActionType.getAllBuys:
            newState.buy = action.payload
            break;
            case BuyActionType.buy:
                const isNotFollowingToUpdate = newState.buy.find(b => b.buyId === action.payload)
                isNotFollowingToUpdate.idBuying = 1;
                isNotFollowingToUpdate.buyCount += 1
                break;
    
            case BuyActionType.unBuy:
                const isFollowingToUpdate = newState.buy.find(b => b.buyId === action.payload)
                if (isFollowingToUpdate.idBuying === 1) {
                    isFollowingToUpdate.idBuying = 0;
                }
                break;
    
            case BuyActionType.RemoveBuy:
                const removeFollowerVacation = newState.movie.find(m => m.movieId === action.payload)
                removeFollowerVacation.buyCount -= 1;
                break;
    }
        return newState
    }

    export const buyStore = createStore(buyReducer);
