import { useEffect, useState } from "react";
import ChairModel from "../../../Models/chair-model";
import "./ChairCard.css";
import appConfig from "../../../Utils/AppConfig";
import React from "react";
import moviesService from "../../../Services/movies-service";
import BuyModel from "../../../Models/buy-model";
import notify from "../../../Utils/Notify";



interface propsCardBuy {
    chair:chai
}

function ChairCard(props1: propsCardBuy): JSX.Element {

    const [chair, setChair] = useState<ChairModel[]>([]);
    const [buys, setBuys] = useState<BuyModel[]>([])

    const [changeColor, setChangeColor] = useState(false);

    const handleClick = () => {
        setChangeColor(!changeColor)
    }

    function chooseChair() {
        try {
            let buyCount = props1.buy.buyCount++;
            let changeCount = props1.buy.chairCount = 1;
            if (changeCount > 0) {
                let chairCount1 = props1.buy.chairCount += props1.buy.chairCount;
                console.log(changeCount)
            }
        } catch (err: any) {
            notify.error(err)
        }

    }

    async function buy(): Promise<void> {
        try {
            moviesService.getAllBuysAfterBuy()
                .then(chair => setBuys(chair))
                .catch(err => alert(err.message))

            // let buyCount1 = props1.buy.buyCount += props1.buy.buyCount;
            let buyCount = props1.buy.chairCount += props1.buy.chairCount;
            // let buyCount3 = props1.buy.price;
            let buyId = props1.buy.chairId += props1.buy.chairId
            // console.log(props.buy.buyCount)
            if (props1.buy.chairCount == 2) {

                console.log("success")
            } else if (props1.buy.chairCount === 8) {
                console.log("fail")
                notify.error("השתמשת בכל הכרטיסים")
            } else if (props1.buy.chairCount > 2) {
                notify.error("הכסא כבר תפוס")
            }
            console.log(buyCount)
        }
        catch (err: any) {
            console.error(err)
        }
    }



    return (
        <div className="ChairCard">
            <React.Fragment>
                <section>
                    <div className="h-screen centered">
                        <button className="btn" onClick={() => buy()}

                        >click me</button>
                    </div>
                </section>

            </React.Fragment>
            <h1></h1>

            <div className="card-content">
                <div className="image-container">
                    <img className="img" src={appConfig.moviesImagesUrl + props1.buy.imageName1} />
                </div>
                <div className="card-title">
                    <h3>{props1.buy.chairNumber} :מספר כסא</h3>
                </div>
            </div>

        </div>

    );
}

export default ChairCard;
