            notify.success("Gift has been deleted");

            // Refresh list: 
            const duplicateGifts = [...product];
            const index = duplicateGifts.findIndex(p => p.productsId === productId);
            duplicateGifts.splice(index, 1);
            setProduct(duplicateGifts);
        }
        catch (err: any) {
            notify.error(err);
        }
    }

    return (
        <div className="DataCard Box">
          Name: {props.movie.name}
          <br />
          Time: {props.movie.time.slice(0, 10).split("-").reverse().join("/")}
          <br />
          Length: {props.movie.length}
          <br />
        </div>
    );
}

export default DataCard;
