import { useState } from "react";
import "./DataCard.css";
import notify from "../../../Utils/Notify";
import { NavLink } from "react-router-dom";
import MoviesModel from "../../../Models/movies-model";
import moviesService from "../../../Services/movies-service";
import appConfig from "../../../Utils/AppConfig";

interface propsCardMovie {
    movie: MoviesModel,
    deleteMe: (movieId: number) => Promise<void>
}

function DataCard(props: propsCardMovie): JSX.Element {

    const [movie, setMovie] = useState<MoviesModel[]>([]);



    async function deleteMe() {
        try {
            if (!window.confirm("Are you sure?")) return;
            await props.deleteMe(props.movie.movieId);
            alert("Movie has been deleted");
        }
        catch (err: any) {
            alert(err.message);
        }
    }

    function isLike(isLike: number): Boolean {
        return isLike === 1 ? true : false;
    }

    async function addLike(like: number) {
        try {
            await moviesService.addLike(like)
        } catch (err: any) {
            notify.error(err)
        }
    }


    return (

        <div className="DataCard">
            <div className="poster">
                <img className="img" src={appConfig.moviesImagesUrl + props.movie.imageName} />
            </div>
            <div className="details">
                <h4>Name: {props.movie.name}
                    <br />
                    Time: {props.movie.time.slice(0, 10).split("-").reverse().join("/")}
                    <br />
                    Length: {props.movie.length}
                    <br />
                    <div className="rating">
                        <i className="fa-solid fa-star">⭐</i>
                        <i className="fa-solid fa-star">⭐</i>
                        <i className="fa-solid fa-star">⭐</i>
                        <i className="fa-solid fa-star">⭐</i>
                        <i className="fa-regular fa-star">⭐</i>
                        <div className="tags">
                            <span>Sci-fi</span>
                            <span>Action</span>
                        </div>
                        {!isLike(props.movie.like) ? <button className="btn btn-outline-primary" onClick={() => addlike(props.movie.like)}>Follow
                        </button> : <button className="btn btn-outline-secondary" onClick={() => unFollow(props.vacation.vacationId)}>Following</button>}<br />Followers: {props.vacation.followersCount}
                    </div>
                    {/* <button onClick={() => deleteMe()}>❌</button> */}
                </h4>
            </div>

        </div>


    );
}

export default DataCard;
