import React from "react";
import { Button, Form, Alert, Row, Col } from "react-bootstrap";
// import "bootstrap/dist/css/bootstrap.min.css";
import Cards from "react-credit-cards";
import "react-credit-cards/es/styles-compiled.css";
import UseForm from "../UseForm/UseForm";
import { min } from "lodash";
import { useForm } from "react-hook-form";
import "./CreditCardForm.css";
import { NavLink, useNavigate } from "react-router-dom";
import notify from "../../../Utils/Notify";
import BuyModel from "../../../Models/buy-model";
import moviesService from "../../../Services/movies-service";
const CreditCardForm = () => {

    const { handleChange , handleFocus, handleSubmit, values, errors } = UseForm();
    // const { register, handleSubmit } = useForm();
    const navigate = useNavigate();

    async function send() {
        try {
            console.log("success")
            // await moviesService.buyMovie(movie)
            notify.success("success")
        } catch (err: any) {
            notify.error("fail")
        }
    }

    return (


        <div className="container ">
            <div className="box justify-content-center align-item-center">
                <div className="form-div Box">
                    <div className="credit-card">
                        <Cards
                            cvc={values.cvc}
                            expiry={values.expiration}
                            focused={values.focus}
                            name={values.name}
                            number={values.number}
                        />
                    </div>
                        <Form onSubmit={handleSubmit}>
                    {/* <form onSubmit={handleSubmit(send)}> */}
                            <Form.Group>
                                <Form.Control
                                    type="text" required minLength={5} maxLength={20}
                                    id="name"
                                    name="name"
                                    placeholder="הכנס שם"
                                    value={values.name}
                                    onChange={handleChange}
                                    onFocus={handleFocus}
                                // isValid={errors.cnumber}
                                />
                            </Form.Group>

                            <Form.Group>
                                <Form.Control
                                    type="number" required min={999999999999999} max={10000000000000000}
                                    id="number"
                                    name="number"
                                    placeholder="הכנס מספר"
                                    value={values.number}
                                    onChange={handleChange}
                                    onFocus={handleFocus}
                                // isValid={errors.cnumber}
                                />
                            </Form.Group>
                            <Row>

                                <Col>
                                    <Form.Group>
                                        <Form.Control
                                            type="number" required min={100} max={1280}
                                            id="expiration"
                                            name="expiration"
                                            placeholder="תוקף"
                                            value={values.expiration}{...register("expiration")}
                                            onChange={handleChange}
                                            onFocus={handleFocus}
                                        // isValid={errors.cexp}
                                        />
                                    </Form.Group>
                                </Col>

                                <Col>
                                    <Form.Group>

                                        <Form.Control
                                            type="number" required min={100} max={999}
                                            id="cvc"
                                            name="cvc"
                                            placeholder="שלוש ספרות"
                                            value={values.cvc}{...register("cvc")}
                                            onChange={handleChange}
                                            onFocus={handleFocus}

                                        // isValid={errors.cexp}
                                        />
                                    </Form.Group>
                                </Col>
                            </Row>
                            {/* <NavLink to={"/home"}>לרכישה</NavLink> */}
                        <button> לרכישה</button>
                    {/* </form> */}
                        </Form>

                </div>
                {/* <Alert
                id="alertMessage" variant={values.name} show={values.number}>
                {errors.message}
            </Alert> */}
            </div>


        </div>
    );



}

export default CreditCardForm;
