import { useState } from "react";
import "./UseForm.css";
// import validateInfo from "./ValidateInfo";

const UseForm = () => {
    const[values, setValues] = useState({
        name:'',
        number: '',
        expiration:'',
        cvc:'',
        focus:''
    })

    if(values.number == ''){

    }

    const[errors, setErrors] = useState()

    const handleFocus = (e: { target: { name: any; }; }) => {
        setValues({
            ...values,
            focus: e.target.name
        })
    }

    const register = (e: { target: { number: any; }; }) => {
        setValues({
            ...values,
            focus: e.target.number
        })
    }

    const handleChange = (e: { target: { name: any; value: any; }; }) => {
        const{name, value} = e.target
        setValues({
            ...values,
            [name]: value
        })
    }

    const handleSubmit1 = (e: { preventDefault: () => void; }) => {
        e.preventDefault()
        // setErrors(validateInfo(values))
    }


    return {handleChange , handleFocus , handleSubmit1 , values ,errors}
}

export default UseForm;
