import { ChangeEvent, SyntheticEvent, useEffect, useState } from "react";
import notify from "../../../Utils/Notify";
import "./DataList.css";
import dataService from "../../../Services/DataService";
import { request } from "http";
import DataCard from "../DataCard/DataCard";
import MovieModel from "../../../Models/movie-model";
import TheatersModel from "../../../Models/theater-model";


function DataList(): JSX.Element {

    const[movies,setMovies] = useState<MovieModel[]>();
    const[theater,setTheater] = useState<TheatersModel[]>([]);

    useEffect(()=>{
        dataService.getAllTheaters()
        .then(theater => setTheater(theater))
        .catch(err => notify.error(err))
    },[])


    async function name(args:ChangeEvent<HTMLSelectElement>) {
        try{
            const theaterId = +args.target.value;
            await dataService.getMovieByTheater(theaterId)
            (theaterId)
        }catch(err:any){
            notify.error(err)
        }
    }

    return (
        <div className="DataList">
            <h1>list</h1>
            <select defaultValue="" onChange={name}>
                <option disabled value="">select theater</option>
            {theater.map(t => <option key={t.theaterId} value={t.theaterId}>{t.theaterName}</option>)}
            </select>
            {movies.map(m => <DataCard key={m.movieId} movie={m}/>)}
        </div>
    );
}

export default DataList;
