import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { useNavigate, useParams } from "react-router-dom";
import notify from "../../../Utils/Notify";
import "./Insert.css";
import dataService from "../../../Services/DataService";
import { request } from "http";
import MovieModel from "../../../Models/movie-model";
import TheatersModel from "../../../Models/theater-model";

function Insert(): JSX.Element {

    const{register,handleSubmit} = useForm<MovieModel>();
    const[movie,setMovie] = useState<MovieModel>();
    const[theater,setTheater] = useState<TheatersModel[]>([]);
    const navigate = useNavigate();

    useEffect(()=>{
        dataService.getAllTheaters()
        .then(theater => setTheater(theater))
        .catch(err => notify.error(err))
    },[])

    async function send(movie:MovieModel) {
        try{
            dataService.addMovie(movie)
            notify.success("the movie add successfully")
            navigate("/list")
        }catch(err:any){
            notify.error(err)
        }
    }

    return (
        <div className="Insert">
            <h1>add</h1>
            <form onSubmit={handleSubmit(send)}>
                <select defaultValue="" {...register("theaterId")}>
                    <option disabled value="">select theater</option>
                    {theater.map(t => <option key={t.theaterId} value={t.theaterId}>{t.theaterName}</option>)}
                </select>
                <label>Name:</label>
                <input type="text"{...register("name")}/>
                <br />
                <label>Time:</label>
                <input type="text"{...register("time")}/>

            </form>
        </div>
    );
}

export default Insert;
