    //             .catch(err => alert(err.message))
    //         // console.log(props.buy.buyCount)
    //         let buyCount2 = props.buy.buyCount;
    //         console.log(buyCount2)

    //     } catch (err: any) {
    //         notify.error(err)
    //     }
    // }, [])


    async function buy(chairId: number): Promise<void> {
        try {
            moviesService.getAllChairsAfterBuy()
                .then(chair => setBuys(chair))
                .catch(err => alert(err.message))
                moviesService.getAllChairsAfterBuy()
                .then(chair => setBuys(chair))
                .catch(err => alert(err.message))
                // console.log(props.buy.buyCount)
            let buyCount = props.chair.chairCount++;
            let buyCount1 = props.buy.buyCount;
            console.log(buyCount1)

            // let priceCount1 = props.buy.price;
            if (props.chair.chairCount == 1) {
                // let priceCount1 = props.chair.chairCount += props.chair.chairCount
                // let buyCount = props.chair.chairCount += props.chair.chairCount;
                // props.chair.chairCount++;
                console.log("success")
            } else if (props.chair.chairCount > 1 || props.chair.buyCount > props.buy.buyCount) {
                console.log("fail")
                notify.error("הכסא כבר תפוס")
            }


            console.log(buyCount)
            // else if (props.chair.buyCount < 0) {
            //     notify.error("הכנס מספר חוקי")
            // }
        }
        catch (err: any) {
            console.error(err)
        }
    }



    return (
        <div className="ChairCard">
            <React.Fragment>
                <section>
                    <div className="h-screen centered">
                        <button className="btn" onClick={() => buy(props.chair.chairId)}

                        >click me</button>
                    </div>
                </section>
            </React.Fragment>
            <h1></h1>

            <div className="card-content">
                <div className="image-container">
                    <img className="img" src={appConfig.moviesImagesUrl + props.chair.imageName} />
                </div>
                <div className="card-title">
                    <h3>{props.chair.chairNumber} :מספר כסא</h3>
                </div>
            </div>
        </div>

    );
}

export default ChairCard;
