import axios from "axios";
import TheatersModel from "../Models/theatersId-model";
import appConfig from "../Utils/AppConfig";
import MoviesModel from "../Models/movies-model";
import { MovieActionType, movieStore } from "../Redux/MovieRedux";
import { TheaterActionType, theaterStore } from "../Redux/TheaterModel";
import { GenreActionType, genreStore } from "../Redux/genreModel";
import GenreModel from "../Models/genre-model";

class MoviesService{

    public async getAllTheaters():Promise<TheatersModel[]>{
        let theater = theaterStore.getState().theater
        if(theater.length === 0){
            const response = await axios.get<TheatersModel[]>(appConfig.theatersUrl)
            theater = response.data;
            console.log(theater)
            theaterStore.dispatch({ type: TheaterActionType.getAllTheaters, payload: theater })
        }
        return theater
    }

  

    public async getAllGenres():Promise<GenreModel[]>{
        let genre = genreStore.getState().genre
        if(genre.length === 0){
            const response = await axios.get<GenreModel[]>(appConfig.genresUrl)
            genre = response.data;
            console.log(genre)
            genreStore.dispatch({ type: GenreActionType.getAllGenres, payload: genre })
        }
        return genre
    }

    public async getAllMovies():Promise<MoviesModel[]>{
        let movie = movieStore.getState().movie
        if(movie.length === 0){
            const response = await axios.get<MoviesModel[]>(appConfig.moviesUrl)
            const movie = response.data;
            console.log(movie)
            movieStore.dispatch({ type: MovieActionType.getAllMovies, payload: movie })
        }
            return movie
        
    }

    public async getMovieByTheater(theaterId:number):Promise<MoviesModel[]>{
        let theaters = movieStore.getState().movie
        let theater = theaters.find(t => t.theaterId === theaterId)  
        if(!theater){
            const response = await axios.get<MoviesModel[]>(appConfig.moviesByTheaterUrl + theaterId);
            theaters = response.data;
            console.log(theaters)
        }
        return theaters;
    }


    public async getOneMovie(movieId:number):Promise<MoviesModel>{
        let movies = movieStore.getState().movie;
        let movie = movies.find(m => m.movieId === movieId)  
        if(!movie){
            const response = await axios.get<MoviesModel>(appConfig.moviesUrl + movieId)
            movie = response.data;
            console.log(movie)
        }
        return movie;
    
    }

    public async addMovie(movie:MoviesModel):Promise<void>{
        // const headers = { "Content-Type": "multipart/form-data" };
        const response = await axios.post<MoviesModel>(appConfig.moviesUrl, movie);
        const newMovie = response.data;
        console.log(newMovie)
        movieStore.dispatch({type:MovieActionType.addMovie, payload: newMovie})
    }

    public async deleteMovie(movieId:number):Promise<void>{
        await axios.delete<MoviesModel>(appConfig.moviesLikesUrl + movieId)
        movieStore.dispatch({type:MovieActionType.deleteMovie, payload: movieId})
    }

    public async addLike(movieId:number):Promise<void>{
        const response = await axios.post(appConfig.moviesLikesUrl + movieId)
        const addedLike = response.data;
        console.log(addedLike)
        movieStore.dispatch({type:MovieActionType.Like, payload: movieId})
        movieStore.dispatch({type:MovieActionType.addLike, payload: movieId})



    }

    public async getMovieByGenre(genreId:number):Promise<MoviesModel[]>{
        let genres = movieStore.getState().movie
        let genre = genres.find(g => g.genreId === genreId)  
        if(!genre){
            const response = await axios.get<MoviesModel[]>(appConfig.moviesByGenreUrl + genreId);
            genres = response.data;
            console.log(genres)
        }
        return genres;
    }

    public async getMoviesActive():Promise<MoviesModel[]>{
        const day = new Date();
        let movies = movieStore.getState().movie;
        const moviesActive = movies.find(m =>  new Date(m.date) <= day && new Date(m.endDate) >= day)
        if(!m){
            const response = await axios.get<MoviesModel[]>(appConfig.moviesByGenreUrl + genreId);
            genres = response.data;
            console.log(genres)
        }
        console.log(moviesActive);
        // await this.getAllMovies();
        movieStore.dispatch({type:MovieActionType.getAllMovies, payload: movies})
        return movies;
        
    } 

}

const moviesService = new MoviesService();
export default moviesService;
