import axios from "axios";
import appConfig from "../Utils/AppConfig";
import { MovieActionType, movieStore } from "../Redux/MovieRedux";
import { BuyActionType, buyStore } from "../Redux/BuyModel";

class BuyService {

    public async addBuy(movieId: number): Promise<void> {
        const response = await axios.post(appConfig.moviesUrl + movieId);
        const addedBuy = response.data;
        buyStore.dispatch({ type: BuyActionType.buy, payload: movieId })
        // vacationStore.dispatch({ type: VacationActionType.AddFollow, payload: vacationId })
    }

    public async unBuy(movieId: number): Promise<void> {
        await axios.delete(appConfig.buyUrl + movieId)
        buyStore.dispatch({ type: BuyActionType.unBuy, payload: movieId })
        buyStore.dispatch({ type: BuyActionType.RemoveBuy, payload: movieId })
    }
}

const buyService = new BuyService();
export default buyService
