import "./BuyMovieCard.css";
import BuyModel from "../../../Models/buy-model";
import React from "react";
import appConfig from "../../../Utils/AppConfig";
import { NavLink } from "react-router-dom";
import buyService from "../../../Services/buy-service";
import moviesService from "../../../Services/movies-service";

interface propsCardBuy {
    buy: BuyModel
}

function BuyMovieCard(props: propsCardBuy) {


    function isFollowing(isFollowing: number): Boolean {
        return isFollowing < 1 ? true : false;
    }

    async function buy(buyId: number): Promise<void> {
        try {
            console.log(props.buy.price)
            props.buy.buyCount = 0;
            await buyService.addBuy(props.buy.buyCount++)
        }
        catch (err: any) {
            console.error(err)
        }
    }

    async function unBuy(buyId: number): Promise<void> {
        try {
            await buyService.unBuy(props.buy.buyId)
        }
        catch (err: any) {
            console.error(err)
        }
    }


    return (
        <div className="BuyMovieCard-container">
            <div className="image-container">
                <img className="img" src={appConfig.moviesImagesUrl + props.buy.imageName} />
            </div>
            <div className="card-content">
                <div className="card-title">
                    <h3>{props.buy.title}</h3>
                </div>
                <div className="card-body">
                    <p>{props.buy.body}</p>
                    <br />
                    <p>{props.buy.food}</p>
                    <p>{props.buy.drink}</p>
                    <p>מחיר:{props.buy.price}  שח </p>
                </div>
                <br />
                { <button className="btn btn-outline-primary"
                    onClick={() => buy(props.buy.buyId)}>Buy
                </button>}
                <br />Buys: {props.buy.buyCount}
                <br />
                <div className="btn">
                    <button>
                        <a>
                            <NavLink to={"/creditCardForm"}>לתשלום</NavLink>
                        </a>
                    </button>
                </div>
            </div>
        </div>
    );
}

export default BuyMovieCard;
