import axios from "axios";
import appConfig from "../Utils/AppConfig";
import TheatersModel from "../Models/theater-model";
import MovieModel from "../Models/movie-model";



class DataService {

    public async getAllTheaters(): Promise<TheatersModel[]> {
        const response = await axios.get<TheatersModel[]>(appConfig.theatersUrl)
        const theater = response.data;
        return theater;
    }

    public async getMovieByTheater(movieId:number):Promise<MovieModel[]>{
        console.log(movie)
        const response = await axios.get<MovieModel[]>(appConfig.moviesByTheaterUrl + movieId);
        const movie = response.data;
        return movie;
    }

    public async addMovie(movie:MovieModel):Promise<void>{
        await axios.post<MovieModel>(appConfig.moviesUrl,movie)
    }

    public async deleteMovie(movieId:number):Promise<void>{
        await axios.delete(appConfig.moviesUrl + movieId)
    }
}

const dataService = new DataService();
export default dataService;
