import { createStore } from "redux";
import MoviesModel from "../Models/movies-model";
import TheatersModel from "../Models/theatersId-model";

export class TheaterState{
    public theater: TheatersModel[] = [];
}

export enum TheaterActionType{
    getAllTheaters = "getAllTheaters",
}

export interface TheaterAction {
    type: TheaterActionType;
    payload: any;
}

export function theaterReducer(currentState = new TheaterState(), action: TheaterAction): TheaterState {
    const newState = { ...currentState }

    switch (action.type) {
        case TheaterActionType.getAllTheaters:
            newState.theater = action.payload
            break;
    }
        return newState
    }

    export const theaterStore = createStore(theaterReducer);
