import "./BuyMovieCard.css";
import BuyModel from "../../../Models/buy-model";
import React, { useEffect, useState } from "react";
import appConfig from "../../../Utils/AppConfig";
import { NavLink } from "react-router-dom";
import buyService from "../../../Services/buy-service";
import moviesService from "../../../Services/movies-service";

interface propsCardBuy {
    buy: BuyModel
}

function BuyMovieCard(props: propsCardBuy) {

    const[buys,setBuys] = useState<BuyModel[]>([])

    function isFollowing(isFollowing: number): Boolean {
        return isFollowing < 1 ? true : false;
    }

    async function buy(buiId:number): Promise<void> {
        try {
            // console.log(props.buy.buyCount)
            let buyCount = props.buy.buyCount++;
            // let priceCount1 = props.buy.price;
            if(props.buy.buyCount > 1){
                let priceCount1 = props.buy.price += props.buy.priceCount;
                console.log(priceCount1)
            }
            console.log(buyCount)
        }
        catch (err: any) {
            console.error(err)
        }
    }

    async function unBuy(buyId: number): Promise<void> {
        try {
            let buyCount = props.buy.buyCount--;
            // let priceCount1 = props.buy.price;
            if(props.buy.buyCount > 0){
                let priceCount1 = props.buy.price -= props.buy.priceCount;
                console.log(priceCount1)
            }
            console.log(buyCount)
        }
        catch (err: any) {
            console.error(err)
        }
    }

    function getAllBuys() {

        // const vacations = +args.target.value;
        moviesService.getAllBuysAfterBuy()
        .then(buy => setBuys(buy))
        .then(buy => console.log(buy))
        .catch(err => alert(err.message))
    }






    return (
        <div className="BuyMovieCard-container">
            <div className="image-container">
                <img className="img" src={appConfig.moviesImagesUrl + props.buy.imageName} />
            </div>
            <div className="card-content">
                <div className="card-title">
                    <h3>{props.buy.title}</h3>
                </div>
                <div className="card-body">
                    <p>{props.buy.body}</p>
                    <br />
                    <p>{props.buy.food}</p>
                    <p>{props.buy.drink}</p>
                    <p>מחיר:{props.buy.price}  שח </p>
                </div>
                <br />
                { <button className="btn btn-outline-primary"
                    onClick={() => buy(props.buy.buyId)}>➕
                </button>}
                <span> | </span>
                { <button className="btn btn-outline-primary"
                    onClick={() => unBuy(props.buy.buyId)}>➖
                </button>}
                <br />
                { <button className="btn btn-outline-primary"
                    onClick={() => getAllBuys()}>  סך הכל בסל
                </button>}
                <br />Buys: {props.buy.buyCount}
                <br />
                <div className="btn">
                    <button>
                        <a>
                            <NavLink to={"/creditCardForm"}>לתשלום</NavLink>
                        </a>
                    </button>
                </div>
            </div>
        </div>
    );
}

export default BuyMovieCard;
