import React from "react";
import { Button, Form, Alert, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import Cards from "react-credit-cards";
import "react-credit-cards/es/styles-compiled.css";
import UseForm from "../UseForm/UseForm";
import { min } from "lodash";
import { useForm } from "react-hook-form";
import "./CreditCardForm.css";
const CreditCardForm = () => {

    const { handleChange, handleFocus, handleSubmit, values, errors } = UseForm();
    const {register} = useForm();


    return (


        <div className="container ">
            <div className="box justify-content-center align-item-center">
                <div className="form-div">
                    <div className="credit-card">
                        <Cards
                            cvc={values.cvc}
                            expiry={values.expiration}
                            focused={values.focus}
                            name={values.name}
                            number={values.number}
                        />
                    </div>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group>
                            <Form.Control
                                type="text" required minLength={5} maxLength={10}
                                id="name"
                                name="name"
                                placeholder="הכנס שם"
                                value={values.name}
                                onChange={handleChange}
                                onFocus={handleFocus}
                                // isValid={errors.cnumber}
                            />
                        </Form.Group>

                        <Form.Group>
                            <Form.Control
                                type="number" re
                                id="number"
                                name= "number"
                                placeholder="הכנס מספר" 
                                value={values.number}  
                                onChange={handleChange}
                                onFocus={handleFocus}
                            // isValid={errors.cnumber}
                            />
                        </Form.Group>
                        <Row>

                            <Col>
                                <Form.Group>
                                    <Form.Control
                                        type="number"
                                        id="expiration"
                                        name="expiration"
                                        placeholder="תוקף"
                                        value={values.expiration}
                                        onChange={handleChange}
                                        onFocus={handleFocus}
                                        // isValid={errors.cexp}
                                        />
                                </Form.Group>
                            </Col>

                            <Col>
                                <Form.Group>

                                    <Form.Control
                                        type="number"
                                        id="cvc"
                                        name="cvc"
                                        placeholder="שלוש ספרות"
                                        value={values.cvc}
                                        onChange={handleChange}
                                        onFocus={handleFocus}
                                        // isValid={errors.cexp}
                                        />
                                </Form.Group>
                            </Col>
                        </Row>
                        <Button id="validateButton" type="submit" >Validate</Button>
                    </Form>
                </div>
                {/* <Alert
                id="alertMessage" variant={errors.variant} show={errors.show}>
                {errors.message}
            </Alert> */}
            </div>


        </div>
    );



}

export default CreditCardForm;
