import { ChangeEvent, useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { useNavigate, useParams } from "react-router-dom";
import notify from "../../../Utils/Notify";
import "./Insert.css";
import { request } from "http";
import MoviesModel from "../../../Models/movies-model";
import moviesService from "../../../Services/movies-service";
import TheatersModel from "../../../Models/theatersId-model";


function Insert(): JSX.Element {

    const[movie,setMovie] = useState<MoviesModel>();
    const[theater,setThread] = useState<TheatersModel[]>([]);
    const{register,handleSubmit} = useForm<MoviesModel>();
    const params = useParams();

    async function send(movie:MoviesModel) {
        try{
            const newMovie = await moviesService.addMovie(movie)
            setMovie(newMovie)
        }catch(err:any){
            notify.error(err)
        }
    }

    useEffect(()=>{
        moviesService.getAllTheaters()
        .then(theater => setThread(theater))
        .catch(err => notify.error(err))
    },[])

    async function name(args:ChangeEvent<HTMLSelectElement>) {
        try{
            const theaterId = +args.target.value;
            const dbTheater = await moviesService.getMovieByTheater(theaterId)
            set(dbTheater)
        }
        catch(err:any){
            notify.error(err)
        }
    }

    return (
        <div className="Insert">
            <h1>add</h1>
            <form onSubmit={handleSubmit(send)}>
                <select defaultValue="" onChange={name}>
                    <option disabled value="">select theater</option>
                    {theater.map(t => <option key={t.theaterId} value={t.theaterId}>{t.theaterName}</option>)}
                </select>
                <label>Name</label>
                <input type="text"{...register("name")}/>
                <br />
                <label>Length</label>
                <input type="text"{...register("length")}/>
                <br />
                <label>Time</label>
                <input type="date"{...register("time")}/>
                <br />
                <button>Add</button>
            </form>
        </div>
    );
}

export default Insert;
