import axios from "axios";
import appConfig from "../Utils/AppConfig";
import { MovieActionType, movieStore } from "../Redux/MovieRedux";
import { BuyActionType } from "../Redux/BuyModel";

class BuyService {

    public async addBuy(bu: number): Promise<void> {
        const response = await axios.post(appConfig.buyUrl + movieId);
        const addedBuy = response.data;
        movieStore.dispatch({ type: BuyActionType.buy, payload: movieId })
        // vacationStore.dispatch({ type: VacationActionType.AddFollow, payload: vacationId })
    }

    public async unBuy(movieId: number): Promise<void> {
        await axios.delete(appConfig.buyUrl + movieId)
        movieStore.dispatch({ type: BuyActionType.unBuy, payload: movieId })
        movieStore.dispatch({ type: BuyActionType.RemoveBuy, payload: movieId })
    }
}

const buyService = new BuyService();
export default buyService
