import { createStore } from "redux";
import MoviesModel from "../Models/movies-model";
import TheatersModel from "../Models/theatersId-model";
import GenreModel from "../Models/genre-model";

export class GenreState{
    public genre: GenreModel[] = [];
}

export enum GenreActionType{
    getAllGenres = "getAllGenres",
}

export interface GenreAction {
    type: GenreActionType;
    payload: any;
}

export function genreReducer(currentState = new GenreState(), action: GenreAction): GenreState {
    const newState = { ...currentState }

    switch (action.type) {
        case GenreActionType.getAllGenres:
            newState.genre = action.payload
            break;
    }
        return newState
    }

    export const genreStore = createStore(genreReducer);
