import { useEffect, useState } from "react";
import ChairModel from "../../../Models/chair-model";
import "./Chairs.css";
import notify from "../../../Utils/Notify";
import moviesService from "../../../Services/movies-service";
import appConfig from "../../../Utils/AppConfig";
import { useParams } from "react-router-dom";
import ChairCard from "../ChairCard/ChairCard";



function Chairs(): JSX.Element {

    const [chair, setChair] = useState<ChairModel[]>([]);
    const params = useParams();

    useEffect(() => {

        moviesService.getAllChairs()
            .then(chair => setChair(chair))
            .then(buy => console.log(buy))
            .catch(err => notify.error(err))
    }, [])


    const [changeColor, setChangeColor] = useState(false);

    const handleClick = () => {
        setChangeColor(!changeColor)
    }


    return (
        <div className="Chairs">
            <h1>chairs</h1>
            <div className="cardDiv">
            <React.Fragment>
                <section>
                    <div className="h-screen centered">
                        <button
                            onClick={handleClick}
                            className={`px-20 py-10 text-3xl capitalize font-bold text-white rounded-lg ${(changeColor === true) ? 'bg-green-700 text-red-500' : 'bg-blue-700'}`}
                        >click me</button>
                    </div>
                </section>
            </React.Fragment>
                {chair.map((item) => <ChairCard key={item.chairId} chair={item} />)}
            </div>
        </div>

    );
}

export default Chairs;
