import { createStore } from "redux";
import MoviesModel from "../Models/movies-model";

export class MovieState{
    public movie: MoviesModel[] = [];
}

export enum MovieActionType{
    getAllMovies = "getAllMovies",
    getMovieByTheater = "getMovieByTheater",
    addMovie = "addMovie",
    deleteMovie = "deleteMovie",
  
}

export interface MovieAction {
    type: MovieActionType;
    payload: any;
}

export function movieReducer(currentState = new MovieState(), action: MovieAction): MovieState {
    const newState = { ...currentState }

    switch (action.type) {
        case MovieActionType.getAllMovies:
            newState.movie = action.payload
            break;

        case MovieActionType.addMovie:
            newState.movie.push(action.payload)

            break;

        case MovieActionType.deleteMovie:
            const indexToDelete = newState.movie.findIndex(p => p.movieId === action.payload)
            if (indexToDelete >= 0) {
                newState.movie.splice(indexToDelete, 1)
            }
            break;

            

        case MovieActionType.buy:
            const isNotFollowingToUpdate = newState.movie.find(m => m.movieId === action.payload)
            isNotFollowingToUpdate.idBuying = 1;
            isNotFollowingToUpdate.buyCount += 1
            break;

        case MovieActionType.unBuy:
            const isFollowingToUpdate = newState.movie.find(m => m.movieId === action.payload)
            if (isFollowingToUpdate.idBuying === 1) {
                isFollowingToUpdate.idBuying = 0;
            }
            break;

        case MovieActionType.RemoveBuy:
            const removeFollowerVacation = newState.movie.find(m => m.movieId === action.payload)
            removeFollowerVacation.buyCount -= 1;
            break;
           
           
        }

        
        
        return newState
    }


    export const movieStore = createStore(movieReducer);
