import { useEffect, useState } from "react";
import ChairModel from "../../../Models/chair-model";
import "./ChairCard.css";
import appConfig from "../../../Utils/AppConfig";
import React from "react";
import moviesService from "../../../Services/movies-service";
import BuyModel from "../../../Models/buy-model";
import notify from "../../../Utils/Notify";

interface propsCardChair {
    chair: ChairModel
    buy: BuyModel
}

function ChairCard(props: propsCardChair): JSX.Element {

    const [chair, setChair] = useState<ChairModel[]>([]);
    const [buys, setBuys] = useState<BuyModel[]>([])

    const [changeColor, setChangeColor] = useState(false);

    const handleClick = () => {
        setChangeColor(!changeColor)
    }

    function chooseChair() {
        try {
            let buyCount = props.chair.buyCount++;
            let changeCount = props.chair.chairCount = 1;
            if (changeCount > 0) {
                let chairCount1 = props.chair.chairCount += props.chair.chairCount;
                console.log(changeCount)
            }
        } catch (err: any) {
            notify.error(err)
        }

    }

    useEffect(() => {
        try {
            moviesService.getAllBuysAfterBuy()
                .then(buy1 => setBuys(buy1))
                .catch(err => alert(err.message))
            let buyCount2 = props.buy.buyCount;

        } catch (err: any) {
            notify.error(err)
        }
    }, [])




    async function buy(chairId: number): Promise<void> {
        try {
            moviesService.getAllBuysAfterBuy()
                .then(chair => setBuys(chair))
                .catch(err => alert(err.message))

            let buyCount = props.chair.chairCount += props.chair.chairCount;
            let buyId = props.chair.chairId += props.chair.chairId
            console.log(props.buy.buyCount)
            if (props.chair.chairCount == 2) {

                console.log("success")
            } else if (props.bu === 4) {
                console.log("fail")
                notify.error("השתמשת בכל הכרטיסים")
            } else if (props.chair.chairCount > 2) {
                notify.error("הכסא כבר תפוס")
            }

            console.log(buyCount)

        }
        catch (err: any) {
            console.error(err)
        }
    }



    return (
        <div className="ChairCard">
            <React.Fragment>
                <section>
                    <div className="h-screen centered">
                        <button className="btn" onClick={() => buy(props.chair.chairId)}

                        >click me</button>
                    </div>
                </section>
            </React.Fragment>
            <h1></h1>

            <div className="card-content">
                <div className="image-container">
                    <img className="img" src={appConfig.moviesImagesUrl + props.chair.imageName} />
                </div>
                <div className="card-title">
                    <h3>{props.chair.chairNumber} :מספר כסא</h3>
                </div>
            </div>

        </div>

    );
}

export default ChairCard;
