import "./WhatInTheater.css";
import { ChangeEvent, useEffect, useState } from "react";
import GenreModel from "../../../Models/genre-model";
import moviesService from "../../../Services/movies-service";
import MoviesModel from "../../../Models/movies-model";
import notify from "../../../Utils/Notify";
import _ from "lodash";
import DataCard from "../DataCard/DataCard";
import { movieStore } from "../../../Redux/MovieRedux";
import Pagination from "../Pagination/Pagination";
import { request } from "http";
import { useParams } from "react-router-dom";


function WhatInTheater(): JSX.Element {



    const day = new Date();
    const [genre, setGenre] = useState<GenreModel[]>([]);
    const [movie, setMovie] = useState<MoviesModel[]>([]);
    // const [currentPage, setCurrentPage] = useState(1);
    // const [postPerPage, setPostPerPage] = useState(4);



    useEffect(() => {
        console.log(movie);
        try {
            moviesService.getAllGenres()

            movieStore.subscribe(() => {
                const dup = [...movieStore.getState().movie];
                setMovie(dup);
            })
        } catch (err: any) {
            alert(err.message)
        }
    }, [])


    function moviesActive() {
        // const vacationActive = +args.target.value;
        moviesService.getMoviesActive()
            .then(moviesActive => setMovie(moviesActive))
            .catch(err => alert(err.message))
    }   

    useEffect(() => {
        moviesService.getAllGenres()
            .then(genre => setGenre(genre))
            .catch(err => notify.error(err))
    }, [])


    async function name(args: ChangeEvent<HTMLSelectElement>) {
        try {
            const genreId = +args.target.value;
            const dbGenre = await moviesService.getMovieByGenre(genreId)
            setMovie(dbGenre)
        }
        catch (err: any) {
            notify.error(err)
        }
    }






    // const indexOfLastPost = currentPage * postPerPage;
    // const indexOfFirstPost = indexOfLastPost - postPerPage;
    // const currentPost = movie.slice(indexOfFirstPost, indexOfLastPost);
    // console.log(currentPost);

    return (
        <div className="WhatInTheater">



            <br />
            {/* <div className="row row-cols-1 row-cols-md-6 g-4 wrapper">
                {currentPost.map((item) => <DataCard key={item.movieId} movie={item} />)}
                <br />
                {<Pagination totalPost={movie.length} postPerPage={postPerPage} currentPage={currentPage} setCurrentPage={setCurrentPage} />}
            </div> */}

            <select defaultValue="" onChange={name}>
                {<option disabled value="">בחרו סוג הקרנה</option>}
                {genre.map(g => <option key={g.genreId} value={g.genreId}>{g.genreName}</option>)}
            </select>
            <select defaultValue="" onClick={moviesActive}>
                <option disabled value="">היום</option>
                <option value={5}>היום</option>
            </select>
            {/* <select defaultValue="" onChange={}>
                {<option disabled value="">בחרו סרט </option>}
                {movie.map(m => <option key={m.movieId} value={m.movieId}>{m.name}</option>)}
            </select> */}
            <br />
            <br />
            <br />
            {movie.map(m => <DataCard key={m.movieId} movie={m} />)}
        </div>
    );
}

export default WhatInTheater;
