import { TheaterActionType, theaterStore } from "../Redux/TheaterModel";
import { ChairActionType, chairStore } from "../Redux/ChairModel";
import { MovieActionType, movieStore } from "../Redux/MovieRedux";
import { GenreActionType, genreStore } from "../Redux/genreModel";
import { BlogActionType, blogStore } from "../Redux/BlogModel";
import { BuyActionType, buyStore } from "../Redux/BuyModel";
import TheatersModel from "../Models/theatersId-model";
import MoviesModel from "../Models/movies-model";
import ChairModel from "../Models/chair-model";
import GenreModel from "../Models/genre-model";
import BlogModel from "../Models/blog-model";
import appConfig from "../Utils/AppConfig";
import BuyModel from "../Models/buy-model";
import axios from "axios";
import _ from "lodash";

class MoviesService {

    async getAllTheaters(): Promise<TheatersModel[]> {
        let theater = theaterStore.getState().theater
        if (theater.length === 0) {
            const response = await axios.get<TheatersModel[]>(appConfig.theatersUrl)
            theater = response.data;
            theaterStore.dispatch({ type: TheaterActionType.getAllTheaters, payload: theater })
        }
        return theater
    }

    async getAllBlogs(): Promise<BlogModel[]> {
        let blog = blogStore.getState().blog
        if (blog.length === 0) {
            const response = await axios.get<BlogModel[]>(appConfig.blogUrl)
            blog = response.data;
            blogStore.dispatch({ type: BlogActionType.getAllBlogs, payload: blog })
        }
        return blog
    }

    async getAllGenres(): Promise<GenreModel[]> {
        let genre = genreStore.getState().genre
        if (genre.length === 0) {
            const response = await axios.get<GenreModel[]>(appConfig.genresUrl)
            genre = response.data;
            genreStore.dispatch({ type: GenreActionType.getAllGenres, payload: genre })
        }
        return genre
    }

    async getAllChairs(): Promise<ChairModel[]> {
        let chair = chairStore.getState().chair;
        if (chair.length === 0) {
            const response = await axios.get<ChairModel[]>(appConfig.chairUrl)
            chair = response.data;
            chairStore.dispatch({ type: ChairActionType.getAllChairs, payload: chair })
        }
        return chair
    }

    async getAllBuys(): Promise<BuyModel[]> {
        let buy = buyStore.getState().buy;
        if (buy.length === 0) {
            const response = await axios.get<BuyModel[]>(appConfig.buyUrl)
            buy = response.data;
            buyStore.dispatch({ type: BuyActionType.getAllBuys, payload: buy })
        }
        return buy
    }

    async getAllBuysAfterBuy(): Promise<BuyModel[]> {
        let buy = await this.getAllBuys();
        const favoriteBuys = buy.filter(b => b.priceCount && b)
        await this.getAllBuys();
        return favoriteBuys;
    }

    async getAllChairsAfterBuy(): Promise<ChairModel[]> {
        await this.getAllBuysAfterBuy();
        let chair = await this.getAllChairs();
        const favoriteChairs = (chair.filter(c => c.chairCount >= 2))
        await this.getAllChairs();
        await this.getAllBuysAfterBuy();
        return favoriteChairs;
    }

    async getAllMovies(): Promise<MoviesModel[]> {
        let movie = movieStore.getState().movie
        if (movie.length === 0) {
            const response = await axios.get<MoviesModel[]>(appConfig.moviesUrl)
            const movie = response.data;
            movieStore.dispatch({ type: MovieActionType.getAllMovies, payload: movie })
        }
        return movie;
    }

    async getMovieByTheater(theaterId: number): Promise<MoviesModel[]> {
        let theaters = movieStore.getState().movie
        let theater = theaters.find(t => t.theaterId === theaterId)
        if (!theater) {
            const response = await axios.get<MoviesModel[]>(appConfig.moviesByTheaterUrl + theaterId);
            theaters = response.data;
        }
        return theaters;
    }

    async getMovieByGenre(genreId: number): Promise<MoviesModel[]> {
        let genres = movieStore.getState().movie
        let genre = genres.find(g => g.genreId === genreId)
        if (!genre) {
            const response = await axios.get<MoviesModel[]>(appConfig.moviesByGenreUrl + genreId);
            genres = response.data;
        }
        return genres;
    }

    public async getOneMovie(movieId: number): Promise<MoviesModel> {
        let movies = movieStore.getState().movie;
        let movie = movies.find(m => m.movieId === movieId)
        if (!movie) {
            const response = await axios.get<MoviesModel>(appConfig.moviesUrl + movieId)
            movie = response.data;
            console.log(movie)
        }
        return movie;

    }

    public async getOneChair(chairId: number): Promise<ChairModel> {
        let chairs = chairStore.getState().chair;
        let chair = chairs.find(c => c.chairId === chairId)
        if (!chair) {
            const response = await axios.get<ChairModel>(appConfig.chairUrl + chairId)
            chair = response.data;
            console.log(chair)
        }
        return chair;

    }

    public async getOneBlog(blogId: number): Promise<BlogModel> {
        let blogs = blogStore.getState().blog;
        let blog = blogs.find(b => b.blogId === blogId)
        if (!blog) {
            const response = await axios.get<BlogModel>(appConfig.blogUrl + blogId)
            blog = response.data;
            console.log(blog)
        }
        return blog;

    }


    public async getOneBuy(buyId: number): Promise<BuyModel> {
        let buys = buyStore.getState().buy;
        let buy = buys.find(b => b.buyId === buyId)
        if (!buy) {
            const response = await axios.get<BuyModel>(appConfig.blogUrl + buyId)
            buy = response.data;
            console.log(buy)
        }
        return buy;

    }

    public async addMovie(movie: MoviesModel): Promise<void> {
        // const headers = { "Content-Type": "multipart/form-data" };
        const response = await axios.post<MoviesModel>(appConfig.moviesUrl, movie);
        const newMovie = response.data;
        console.log(newMovie)
        movieStore.dispatch({ type: MovieActionType.addMovie, payload: newMovie })
    }

    public async deleteMovie(movieId: number): Promise<void> {
        await axios.delete<MoviesModel>(appConfig.moviesLikesUrl + movieId)
        movieStore.dispatch({ type: MovieActionType.deleteMovie, payload: movieId })
    }

    // public async addLike(movieId:number):Promise<void>{
    //     const response = await axios.post(appConfig.moviesLikesUrl + movieId)
    //     const addedLike = response.data;
    //     console.log(addedLike)
    //     movieStore.dispatch({type:MovieActionType.Like, payload: movieId})
    //     movieStore.dispatch({type:MovieActionType.addLike, payload: movieId})



    // }


    public async getMoviesActive(): Promise<MoviesModel[]> {
        const day = new Date();
        console.log(day);
        let movies = await this.getAllMovies();
        const moviesActive = movies.filter(m => new Date(m.date) <= day && new Date(m.endDate) <= day)
        console.log(moviesActive);
        await this.getAllMovies();
        return moviesActive;

    }

    public async buyMovie(user: BuyModel): Promise<void> {

        // Send user to backend:
        const response = await axios.post<string>(appConfig.loginUrl, user);

        // Get the returned token:
        const buy = response.data;

        console.log(buy);

        // Send token to global state:
        // authStore.dispatch({ type: AuthActionType.Register, payload: token });
    }

}

const moviesService = new MoviesService();
export default moviesService;
